import React, { useState } from "react";
import "./InvoiceForm.css";
import html2canvas from "html2canvas";
import jsPDF from "jspdf";

function InvoiceForm() {
  const [invoiceDate, setInvoiceDate] = useState("16 June 2025");
  const [invoiceNumber, setInvoiceNumber] = useState("12345");

  const [billTo, setBillTo] = useState({ name: "", contact: "" });
  const [paymentInfo, setPaymentInfo] = useState({
    title: "Payment Information",
    studio: "",
    bank: "",
    account: "",
  });
  const [senderInfo, setSenderInfo] = useState({
    name: "",
    address: "",
    phone: "",
    email: "",
  });

  const [items, setItems] = useState([{ description: "", qty: 1, price: 0 }]);
  const [discount, setDiscount] = useState(0);

  const handleItemChange = (index, field, value) => {
    const updated = [...items];
    updated[index][field] =
      field === "description" ? value : Number(value) || 0;
    setItems(updated);
  };

  const addItem = () => setItems([...items, { description: "", qty: 1, price: 0 }]);
  const removeItem = (index) => {
    const updated = [...items];
    updated.splice(index, 1);
    setItems(updated);
  };

  const subtotal = items.reduce((acc, item) => acc + Number(item.qty) * Number(item.price), 0);
  const cgst = subtotal * 0.09;
  const sgst = subtotal * 0.09;
  const total = subtotal + cgst + sgst - discount;

  const downloadPDF = () => {
    const input = document.getElementById("invoice");
    html2canvas(input, { scale: 2 }).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdf = new jsPDF("p", "mm", "a4");
      const imgProps = pdf.getImageProperties(imgData);
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;
      pdf.addImage(imgData, "PNG", 0, 0, pdfWidth, pdfHeight);
      pdf.save(`invoice-${invoiceNumber || "download"}.pdf`);
    });
  };

  const printInvoice = () => window.print();

  return (
    <div className="invoice-container">
      <div id="invoice">
        <div className="header">
          <div className="left">
            <input
              type="text"
              value={invoiceDate}
              onChange={(e) => setInvoiceDate(e.target.value)}
            />
            <input
              type="text"
              value={invoiceNumber}
              onChange={(e) => setInvoiceNumber(e.target.value)}
              placeholder="Invoice Number"
            />
          </div>
          <h1>Invoice</h1>
        </div>

        <div className="bill-to">
          <h3>Bill To:</h3>
          <input
            type="text"
            value={billTo.name}
            onChange={(e) => setBillTo({ ...billTo, name: e.target.value })}
            placeholder="Client Name"
          />
          <input
            type="text"
            value={billTo.contact}
            onChange={(e) => setBillTo({ ...billTo, contact: e.target.value })}
            placeholder="Phone or Email"
          />
        </div>

        <table className="invoice-table">
          <thead>
            <tr>
              <th>Description</th>
              <th>Qty</th>
              <th>Price</th>
              <th>Subtotal</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {items.map((item, index) => (
              <tr key={index}>
                <td>
                  <input
                    type="text"
                    value={item.description}
                    onChange={(e) => handleItemChange(index, "description", e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={item.qty}
                    onChange={(e) => handleItemChange(index, "qty", e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={item.price}
                    onChange={(e) => handleItemChange(index, "price", e.target.value)}
                  />
                </td>
                <td>₹{(Number(item.qty) * Number(item.price)).toFixed(2)}</td>
                <td>
                  <button className="no-print" onClick={() => removeItem(index)}>✕</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <button onClick={addItem} className="add-btn no-print">+ Add Item</button>

        <div className="summary">
          <p>Subtotal: ₹{subtotal.toFixed(2)}</p>
          <p>CGST (9%): ₹{cgst.toFixed(2)}</p>
          <p>SGST (9%): ₹{sgst.toFixed(2)}</p>
          <p>
            Discount:
            <input
              type="number"
              value={discount}
              onChange={(e) => setDiscount(Number(e.target.value) || 0)}
            />
          </p>
          <h3>Total: ₹{total.toFixed(2)}</h3>
        </div>

        <div className="footer">
          <div>
            <input
              type="text"
              placeholder="Payment Info Title"
              value={paymentInfo.title}
              onChange={(e) => setPaymentInfo({ ...paymentInfo, title: e.target.value })}
            />
            <input
              type="text"
              placeholder="Studio / Company Name"
              value={paymentInfo.studio}
              onChange={(e) => setPaymentInfo({ ...paymentInfo, studio: e.target.value })}
            />
            <input
              type="text"
              placeholder="Bank Name"
              value={paymentInfo.bank}
              onChange={(e) => setPaymentInfo({ ...paymentInfo, bank: e.target.value })}
            />
            <input
              type="text"
              placeholder="Account Number"
              value={paymentInfo.account}
              onChange={(e) => setPaymentInfo({ ...paymentInfo, account: e.target.value })}
            />
          </div>
          <div>
            <h4>Your Details</h4>
            <input
              type="text"
              placeholder="Your Name"
              value={senderInfo.name}
              onChange={(e) => setSenderInfo({ ...senderInfo, name: e.target.value })}
            />
            <input
              type="text"
              placeholder="Your Address"
              value={senderInfo.address}
              onChange={(e) => setSenderInfo({ ...senderInfo, address: e.target.value })}
            />
            <input
              type="text"
              placeholder="Your Phone"
              value={senderInfo.phone}
              onChange={(e) => setSenderInfo({ ...senderInfo, phone: e.target.value })}
            />
            <input
              type="email"
              placeholder="Your Email"
              value={senderInfo.email}
              onChange={(e) => setSenderInfo({ ...senderInfo, email: e.target.value })}
            />
          </div>
        </div>
      </div>

      <div className="invoice-actions no-print">
        <button onClick={downloadPDF}>Download as PDF</button>
        <button onClick={printInvoice}>Print Invoice</button>
      </div>
    </div>
  );
}

export default InvoiceForm;
